import * as React from "react";
import styled from "@emotion/styled";
require("@emotion/types");

function Input({
  message,
  setMessage,
  sendMessage,
}: {
  message: {};
  setMessage: React.Dispatch<React.SetStateAction<{}>>;
  sendMessage: (event: { preventDefault: () => void }) => void;
}) {
  return (
    <FormContainer>
      <InputWrapper
        type="text"
        value={message}
        onChange={(event: { target: { value: React.SetStateAction<{}> } }) =>
          setMessage(event.target.value)
        }
        onKeyPress={(event: any) =>
          event.key === "Enter" ? sendMessage(event) : null
        }
      />
      <SendButton onClick={(event: any) => sendMessage(event)}>
        Send{" "}
      </SendButton>
    </FormContainer>
  );
}

export default Input;

const FormContainer = styled.form`
  background-color: lightslategray;
  display: flex;

  width: 100%;
  border-radius: 0px 0px 5px 5px;
  margin-bottom: 5 px;
`;

const InputWrapper = styled.input`
  width: 80%;
  font-size: 14px;
  margin: 5px;
  margin-left: 10px;
  box-shadow: none;
`;

const SendButton = styled.button`
  margin: 5px;
  background-color: darkslategrey;
  color: white;
  border: 0;
  background: none;
  box-shadow: none;
  border-radius: 0px;
`;
