import React from "react";
import Message from "../Message";

import styled from "@emotion/styled";
const ScrollToBottom = require("react-scroll-to-bottom");

function Messages({ messages, name }: { messages: any; name: any }) {
  return (
    <MessagesContainer>
      {messages.map(
        (message: { user: any; text: any }, i: string | number | undefined) => (
          <div key={i}>
            <Message message={message} name={name} />
          </div>
        )
      )}
    </MessagesContainer>
  );
}
export default Messages;

const MessagesContainer = styled(ScrollToBottom)`
  width: 100%;
  height: 80%;
  display: flex;
  flex-direction: column;
`;
