import React from "react";
import { useState, useEffect } from "react";
import io from "socket.io-client";
import InfoBar from "../InfoBar";
import Input from "../Input/Input";
import Messages from "../Messages";
import styled from "@emotion/styled";
const queryString = require("query-string");

type inputValue = string | number | string[] | undefined;

let socket: SocketIOClient.Socket;

function Chat({ location }: { location: String }) {
  const [name, setName] = useState<String | String[]>("");
  const [room, setRoom] = useState<String | String[]>("");
  const [currentMessage, setCurrentMessage] = useState<inputValue>("");
  const [messages, setMessages] = useState<{}[]>([]);
  const ENDPOINT = "localhost:5000";

  useEffect(() => {
    const { name, room } = queryString.parse(location.search);
    socket = io("localhost:5000");

    setName(name);
    setRoom(room);

    socket.emit("join", { name, room }, () => {});
    return () => {
      socket.emit("disconnect");
      // socket.off();
    };
  }, [ENDPOINT, location.search]);

  useEffect(() => {
    socket.on("message", (message: {}) => {
      setMessages([...messages, message]);
    });
  }, [messages]);

  function sendMessage(event: { preventDefault: () => void }) {
    event.preventDefault();
    if (currentMessage) {
      socket.emit("sendMessage", currentMessage, () => setCurrentMessage(""));
    }
  }

  console.log(currentMessage, messages);
