import React from "react";
import styled from "@emotion/styled";

type inputType = string | number | string[] | undefined;

function Input({
  currentMessage,
  setCurrentMessage,
  sendMessage,
}: {
  currentMessage: inputType;
  setCurrentMessage: React.Dispatch<React.SetStateAction<inputValue>>;
  sendMessage: (event: { preventDefault: () => void }) => void;
}) {
  return (
    <FormContainer>
      <InputWrapper
        type="text"
        value={currentMessage}
        onChange={(event: { target: { value: string } }) =>
          setCurrentMessage(event.target.value)
        }
        onKeyPress={(event: any) =>
          event.key === "Enter" ? sendMessage(event) : null
        }
      />
      <SendButton onClick={(event: any) => sendMessage(event)}>Send</SendButton>
    </FormContainer>
  );
}

export default Input;

const FormContainer = styled.form`
  background-color: lightslategray;
  display: flex;

  width: 100%;
  border-radius: 0px 0px 5px 5px;
  margin-bottom: 5 px;
`;

const InputWrapper = styled.input`
  width: 80%;
  font-size: 14px;
  margin: 5px;
  margin-left: 10px;
  box-shadow: none;
`;

const SendButton = styled.button`
  margin: 5px;
  background-color: darkslategrey;
  color: white;
  border: 0;
  background: none;
  box-shadow: none;
  border-radius: 0px;
`;
