import React from "react";
import { useState, useEffect } from "react";
import io from "socket.io-client";
import InfoBar from "../InfoBar";
import Input from "../Input/Input";
import Messages from "../Messages";
import styled from "@emotion/styled";
const queryString = require("query-string");

type inputValue = string | number | string[] | undefined;

let socket: SocketIOClient.Socket;

function Chat({ location }: { location: String }) {
  const [name, setName] = useState<String | String[]>("");
  const [room, setRoom] = useState<String | String[]>("");
  const [currentMessage, setCurrentMessage] = useState<inputValue>("");
  const [messages, setMessages] = useState<{}[]>([]);
  const ENDPOINT = "localhost:5000";

  useEffect(() => {
    const { name, room } = queryString.parse(location.search);
    socket = io("localhost:5000");

    setName(name);
    setRoom(room);

    socket.emit("join", { name, room }, () => {});
    return () => {
      socket.emit("disconnect");
      // socket.off();
    };
  }, [ENDPOINT, location.search]);

  useEffect(() => {
    socket.on("message", (message: {}) => {
      setMessages([...messages, message]);
    });
  }, [messages]);

  function sendMessage(event: { preventDefault: () => void }) {
    event.preventDefault();
    if (currentMessage) {
      socket.emit("sendMessage", currentMessage, () => setCurrentMessage(""));
    }
  }

  console.log(currentMessage, messages);
  return (
    <Container>
      <ChatWrapper>
        <InfoBar room={room}></InfoBar>
        <Messages messages={messages} name={name} />
        <Input
          currentMessage={currentMessage}
          setCurrentMessage={setCurrentMessage}
          sendMessage={sendMessage}
        />
      </ChatWrapper>
    </Container>
  );
}
export default Chat;

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: #03bb85;
`;

const ChatWrapper = styled.div`
  height: 600px;
  width: 400px;
  background: white;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: column;
  border-radius: 5px;
  box-shadow: 0 2px 3px #000;
`;
