import React, { useState } from "react";
import styled from "@emotion/styled";
import { RootStateOrAny, connect } from "react-redux";
import { doSetCurrentMessage, doSendMessage } from "./ducks/actions";

const select = (state: RootStateOrAny) => ({
  currentMessage: state.currentMessage,
});

const actions = {
  setCurrentMessage: doSetCurrentMessage,
  sendMessage: doSendMessage,
};

function Input({ sendMessage }: { sendMessage: Function }) {
  const [currentMessage, setCurrentMessage] = useState("");
  return (
    <FormContainer>
      <InputWrapper
        type="text"
        value={currentMessage}
        onChange={(event: any) => setCurrentMessage(event.target.valuel)}
        onKeyPress={(event: React.KeyboardEvent<HTMLInputElement>) => {
          event.preventDefault();
          return event.key === "Enter" ? sendMessage(event.target) : null;
        }}
      />
      <SendButton
        onClick={(event: any) => {
          sendMessage(event.target.value);
          setCurrentMessage("");
        }}
      >
        Send
      </SendButton>
    </FormContainer>
  );
}

export default connect(select, actions)(Input);

const FormContainer = styled.form`
  background-color: lightslategray;
  display: flex;

  width: 100%;
  border-radius: 0px 0px 5px 5px;
  margin-bottom: 5 px;
`;

const InputWrapper = styled.input`
  width: 80%;
  font-size: 14px;
  margin: 5px;
  margin-left: 10px;
  box-shadow: none;
`;

const SendButton = styled.button`
  margin: 5px;
  background-color: darkslategrey;
  color: white;
  border: 0;
  background: none;
  box-shadow: none;
  border-radius: 0px;
`;
